mapper.select:
select CAST(t.campaignId AS CHAR) campaignId, CAST(t.adGroupId AS CHAR) id,
	CAST(t.profileid AS CHAR) profileid, t.name,mk.name market,ifnull(t.state,'enabled') status,
	tr.iswarn iswarn,tr.cycle,tr.fcondition,tr.subtrahend,tr.quota,tr.amount,
	tk.remark,p.marketplaceId marketplaceid,CAST(a.groupid AS CHAR) groupid,tg.name groupname,
	v.*, tc.name campaignName,'' targetingtype,t.opttime,'SB' campaignType
from t_amz_adv_adgroups_hsa t
left join(
	select t.adGroupid,t.campaignId campaignId1,t.profileid profileid1,
		sum(t.impressions) impressions,sum(t.clicks) clicks,sum(t.cost) cost,
		ifnull((sum(t.clicks) / sum(t.impressions)),0) CTR,
		ifnull((sum(t.cost) / sum(t.clicks)),0) avgcost,
		ifnull(sum(a.attributedSales14d) - sum(a.attributedSales14dSameSKU),0) attributedSales,
		sum(a.attributedSales14dSameSKU) attributedSalesSameSKU,
		ifnull(sum(a.attributedConversions14d)-sum(a.attributedConversions14dSameSKU),0) attributedConversions,
		sum(a.attributedConversions14dSameSKU) attributedConversionsSameSKU,
		sum(a.attributedConversions14d) attributedUnitsOrdered,
		sum(a.attributedSales14d) sumSales,
		sum(a.attributedConversions14d) sumUnits,
		ifnull((sum(a.attributedConversions14d) / sum(t.clicks)),0) CSRT,
		case when ifnull(sum(t.cost),0)>0 and ifnull(sum(a.attributedSales14d),0)=0
		then 10000000000
		else ifnull((sum(t.cost) / sum(a.attributedSales14d)),0)
		end ACOS,
		ifnull((sum(a.attributedSales14d) / sum(t.cost)),0) ROAS,

		ifnull(sum(a.attributedSales14d) - sum(a.attributedSales14dSameSKU),0) attributedSales14d,
		sum(a.attributedSales14dSameSKU) attributedSalesSameSKU14d,
		ifnull(sum(a.attributedConversions14d)-sum(a.attributedConversions14dSameSKU),0) attributedConversions14d,
		sum(a.attributedConversions14dSameSKU) attributedUnitsOrderedSameSKU14d,
		sum(a.attributedConversions14d) attributedUnitsOrdered14d,
		sum(a.attributedSales14d) sumSales14d,
		sum(a.attributedConversions14d) sumUnits14d,
		ifnull((sum(a.attributedConversions14d) / sum(t.clicks)),0) CSRT14d,
		case when ifnull(sum(t.cost),0)>0 and ifnull(sum(a.attributedSales14d),0)=0
		then 10000000000
		else ifnull((sum(t.cost) / sum(a.attributedSales14d)),0)
		end ACOS14d,
		ifnull((sum(a.attributedSales14d) / sum(t.cost)),0) ROAS14d
	from t_amz_adv_rpt2_hsa_adgroups t
    left join t_amz_adv_rpt2_hsa_adgroups_attributed a on a.adGroupId=t.adGroupId and a.bydate=t.bydate
    left join t_amz_adv_rpt2_hsa_adgroups_brand r on r.adGroupId=t.adGroupId and r.bydate=t.bydate
    left join t_amz_adv_rpt2_hsa_adgroups_video v on  v.adGroupId=t.adGroupId and v.bydate=t.bydate
	where t.bydate >= #{fromDate,jdbcType=DATE} and t.bydate <= #{endDate,jdbcType=DATE}

		and t.profileid = #{profileid,jdbcType=BIGINT}


		and t.campaignId = #{campaignid,jdbcType=BIGINT}


		and t.adGroupId = #{adGroupid,jdbcType=BIGINT}

	group by t.adGroupid,campaignId1,profileid1
) v on t.adGroupId= v.adGroupid and t.campaignId = v.campaignId1 and t.profileid = v.profileid1
left join t_amz_adv_profile p on t.profileid = p.id
left join t_amz_adv_auth a on a.id=p.advauthId and a.disable = 0
left join t_amazon_group tg on tg.id = a.groupid
left join t_marketplace mk on mk.marketplaceId=p.marketplaceId
left join t_amz_adv_campaigns_hsa tc on t.campaignId = tc.campaignId and t.profileid = tc.profileid
left join t_amz_adv_remind tr on tr.adgroupid = t.adGroupId and t.campaignId = tr.campaignid and p.id = tr.profileid 
		and tr.keywordid = 0 and tr.adid = 0 and tr.targetid = 0
left join t_amz_adv_remark tk on tk.adgroupId = t.adGroupId and t.campaignId = tk.campaignId and p.id = tk.profileid 
		and tk.keywordId = 0 and tk.adId = 0 and tk.targetId = 0
where a.shopid = #{shopid,jdbcType=CHAR}

	and t.adGroupId = #{adGroupid,jdbcType=BIGINT}


	and t.campaignId = #{campaignid,jdbcType=BIGINT}


	and tc.name like #{campaignName,jdbcType=CHAR}


	and tc.state = #{campaignStatus,jdbcType=CHAR}


	and tc.targetingType = #{targetingType,jdbcType=CHAR}


	and tc.portfolioid = #{portfolios,jdbcType=BIGINT}


	and t.profileid = #{profileid,jdbcType=BIGINT}


	and p.marketplaceId= #{marketplaceid,jdbcType=CHAR}


	and a.groupid = #{groupid,jdbcType=CHAR}


	and (t.state = 'paused' or t.state = 'enabled' or t.state = 'PAUSED' or t.state = 'ENABLED' or t.state is null)


	and t.name like #{adGroupsName,jdbcType=CHAR}


	and ${paralist}

select sum(impressions) impressions, sum(clicks) clicks, sum(cost) cost,
	ifnull((sum(cost) / sum(clicks)),0) avgcost,
	ifnull((sum(clicks) / sum(impressions)),0) CTR,
	case when ifnull(sum(cost),0)>0 and ifnull(sum(ifnull(attributedSales14d,0)),0)=0
	then 10000000000
	else ifnull((sum(cost) / sum(ifnull(attributedSales14d,0))),0)
	end ACOS,
	ifnull((sum(ifnull(attributedSales14d,0)) / sum(cost)),0) ROAS,
	ifnull(sum(ifnull(attributedSales14d,0)),0) sumSales,
	ifnull(sum(ifnull(attributedConversions14d,0)),0) sumUnits,
	ifnull((sum(attributedConversions14d) / sum(clicks)),0) CSRT,
	ifnull(sum(attributedConversions14d) - sum(attributedConversions14dSameSKU),0) attributedConversions,
	ifnull(sum(attributedConversions14dSameSKU),0) attributedConversionsSameSKU,
	ifnull(sum(attributedConversions14d),0) attributedUnitsOrdered,
	ifnull(sum(attributedSales14dSameSKU),0) attributedSalesSameSKU,
	ifnull(sum(attributedSales14d) - sum(attributedSales14dSameSKU),0) attributedSales
from t_amz_adv_rpt2_hsa_adgroups v
left join t_amz_adv_rpt2_hsa_adgroups_attributed d on d.adGroupId=v.adGroupId and d.bydate=v.bydate
left join t_amz_adv_rpt2_hsa_adgroups_brand sa on sa.adGroupId=v.adGroupId and sa.bydate=v.bydate
left join t_amz_adv_rpt2_hsa_adgroups_video v1 on  v1.adGroupId=v.adGroupId and v1.bydate=v.bydate
left join t_amz_adv_adgroups_hsa t on t.adGroupId = v.adGroupId and t.campaignId = v.campaignId and t.profileid = v.profileid
left join t_amz_adv_campaigns_hsa tc on t.campaignId = tc.campaignId and t.profileid = tc.profileid
left join t_amz_adv_profile p on t.profileid = p.id
left join t_amz_adv_auth a on a.id=p.advauthId and a.disable = 0
left join t_amazon_group tg on tg.id = a.groupid
left join t_marketplace mk on mk.marketplaceId=p.marketplaceId
where v.bydate >= #{fromDate,jdbcType=DATE}  and v.bydate <= #{endDate,jdbcType=DATE} and a.shopid = #{shopid,jdbcType=CHAR}

	and v.adGroupId = #{adGroupid,jdbcType=BIGINT}


	and v.campaignId = #{campaignid,jdbcType=BIGINT}


	and tc.name like #{campaignName,jdbcType=CHAR}


	and tc.state = #{campaignStatus,jdbcType=CHAR}


	and tc.targetingType = #{targetingType,jdbcType=CHAR}


	and v.profileid = #{profileid,jdbcType=BIGINT}


	and p.marketplaceId= #{marketplaceid,jdbcType=CHAR}


	and a.groupid = #{groupid,jdbcType=CHAR}


	and t.state = #{adGroupsStatus,jdbcType=CHAR}


	and t.state = 'enabled'
	and tc.state = 'enabled'


	and (t.state = 'paused' or (t.state = 'enabled' 
	and (tc.state = 'paused' or tc.state = 'archived')))


	and (t.state = 'paused' or t.state = 'enabled' or t.state = 'PAUSED' or t.state = 'ENABLED' or t.state is null)


	and t.name like #{adGroupsName,jdbcType=CHAR}


	and ${paralist}

select

	date_format(v.bydate,'%m-%d') bydate,


	date_format(subdate(v.bydate,date_format(v.bydate,'%w')-7),'%m-%d') bydate,


	concat( year(v.bydate) ,'-' ,month(v.bydate)) bydate,


	${serchlist}

from t_amz_adv_rpt2_hsa_adgroups v
left join t_amz_adv_rpt2_hsa_adgroups_attributed d on d.adGroupId=v.adGroupId and d.bydate=v.bydate
left join t_amz_adv_rpt2_hsa_adgroups_brand sa on sa.adGroupId=v.adGroupId and sa.bydate=v.bydate
left join t_amz_adv_rpt2_hsa_adgroups_video vo on vo.adGroupId=v.adGroupId and vo.bydate=v.bydate
left join t_amz_adv_adgroups_hsa t on t.adGroupId= v.adGroupId and t.campaignId = v.campaignId and t.profileid = v.profileid
left join t_amz_adv_campaigns_hsa tc on v.campaignId = tc.campaignId and v.profileid = tc.profileid
left join t_amz_adv_profile p on t.profileid = p.id
left join t_amz_adv_auth a on a.id=p.advauthId and a.disable = 0
left join t_amazon_group tg on tg.id = a.groupid
where a.shopid = #{shopid,jdbcType=CHAR}
	and v.bydate >= #{fromDate,jdbcType=DATE} and v.bydate <= #{endDate,jdbcType=DATE}

	and t.profileid = #{profileid,jdbcType=BIGINT}


	and p.marketplaceId= #{marketplaceid,jdbcType=CHAR}


	and a.groupid = #{groupid,jdbcType=CHAR}


	and v.campaignId = #{campaignid,jdbcType=BIGINT}


	and v.adGroupId = #{adGroupid,jdbcType=BIGINT}


	and tc.name like #{campaignName,jdbcType=CHAR}


	and tc.state = #{campaignStatus,jdbcType=CHAR}


	and tc.targetingType = #{targetingType,jdbcType=CHAR}


	and t.state = #{adGroupsStatus,jdbcType=CHAR}


	and t.state = 'enabled'
	and tc.state = 'enabled'


	and (t.state = 'paused' or (t.state = 'enabled' 
	and (tc.state = 'paused' or tc.state = 'archived')))


	and (t.state = 'paused' or t.state = 'enabled' or t.state = 'PAUSED' or t.state = 'ENABLED' or t.state is null)


	and t.name like #{adGroupsName,jdbcType=CHAR}


	group by date_format(v.bydate,'%y-%m-%d')


	group by date_format(subdate(v.bydate,date_format(v.bydate,'%w')-7),'%y-%m-%d')


	group by concat( year(v.bydate) ,'-' ,month(v.bydate))
