select CAST(t.adGroupId AS CHAR) adGroupId, CAST(t.campaignId AS CHAR) campaignId, CAST(t.targetId AS CHAR) id, CAST(t.profileid AS CHAR) profileid, t.expressionType, t.expression,t.bid bid, tr.cycle,tr.fcondition,tr.subtrahend,tr.quota,tr.amount, t.state status,mk.name market,tk.remark,tr.iswarn iswarn,p.marketplaceId marketplaceid, a.groupid groupid,tg.name groupname,v.*,tc.name campaignName,tc.state campaignStatus, tgp.name adGroupName,tgp.state adGroupStatus ,t.opttime from t_amz_adv_product_targe_hsa t left join( SELECT t.campaignId campaignId1, t.profileid profileid1, t.adGroupId adGroupId1, t.targetId targetId, SUM(impressions) impressions, SUM(clicks) clicks, SUM(cost) cost, pd.expression targetingText, IFNULL((SUM(cost) / SUM(clicks)),0) avgcost, IFNULL((SUM(clicks) / SUM(impressions)),0) CTR, CASE WHEN IFNULL(SUM(cost),0)> 0 AND IFNULL(SUM(attributedSales14d),0)= 0 THEN 10000000000 ELSE IFNULL( ( SUM(cost) / SUM(attributedSales14d) ), 0 ) END ACOS, IFNULL( ( SUM(attributedSales14d) / SUM(cost) ), 0 ) ROAS, IFNULL( SUM(attributedSales14d),0) sumSales, IFNULL( SUM(attributedConversions14d),0) sumUnits, IFNULL( (SUM(attributedConversions14d) / SUM(clicks)),0 ) CSRT, IFNULL( SUM(attributedConversions14d) - SUM(attributedConversions14dSameSKU ),0) attributedConversions, IFNULL( SUM(attributedConversions14dSameSKU ),0) attributedConversionsSameSKU, IFNULL( SUM(attributedConversions14d),0) attributedUnitsOrdered, IFNULL( SUM(attributedSales14dSameSKU), 0) attributedSalesSameSKU, IFNULL(SUM(attributedSales14d) - SUM(attributedSales14dSameSKU),0) attributedSales FROM t_amz_adv_rpt2_hsa_product_targets t LEFT JOIN t_amz_adv_rpt2_hsa_product_targets_attributed d ON d.targetId = t.targetId AND d.bydate = t.bydate LEFT JOIN t_amz_adv_rpt2_hsa_product_targets_brand bd ON bd.targetId = t.targetId AND bd.bydate = t.bydate LEFT JOIN t_amz_adv_rpt2_hsa_product_targets_video vd ON vd.targetId = t.targetId AND vd.bydate = t.bydate LEFT JOIN t_amz_adv_product_targe_hsa pd ON pd.adGroupId = t.adGroupId AND pd.campaignId = t.campaignId AND pd.targetId = t.targetId AND pd.profileid = t.profileid LEFT JOIN t_amz_adv_adgroups ad ON ad.adGroupId = t.adGroupId AND ad.campaignId = t.campaignId AND t.profileid = ad.profileid LEFT JOIN t_amz_adv_campaigns cm ON cm.campaignId = t.campaignId AND cm.profileid = t.profileid WHERE t.bydate >= #{fromDate,jdbcType=DATE} and t.bydate <= #{endDate,jdbcType=DATE}
and t.profileid = #{profileid,jdbcType=BIGINT}
and t.campaignId = #{campaignid,jdbcType=BIGINT}
and t.adGroupId = #{adGroupid,jdbcType=BIGINT}
group by adGroupId1,campaignId1,profileid1,targetId ) v on t.targetId = v.targetId and t.adGroupId= v.adGroupId1 and t.campaignId = v.campaignId1 and t.profileid = v.profileid1 left join t_amz_adv_profile p on t.profileid = p.id left join t_amz_adv_auth a on a.id=p.advauthId and a.disable = 0 left join t_amazon_group tg on tg.id = a.groupid left join t_marketplace mk on mk.marketplaceId=p.marketplaceId left join t_amz_adv_campaigns_hsa tc on t.campaignId = tc.campaignId and t.profileid = tc.profileid left join t_amz_adv_adgroups_hsa tgp on t.adGroupId = tgp.adGroupId and t.campaignId = tgp.campaignId and t.profileid = tgp.profileid left join t_amz_adv_remind tr on tr.targetid = t.targetId and tr.adgroupid = t.adGroupId and t.campaignId = tr.campaignid and p.id = tr.profileid and tr.adid = 0 and tr.keywordid = 0 left join t_amz_adv_remark tk on tk.targetid = t.targetId and tk.adgroupid = t.adGroupId and t.campaignId = tk.campaignId and p.id = tk.profileid and tk.adid = 0 and tk.keywordid = 0 where a.shopid = #{shopid,jdbcType=CHAR}
and t.campaignId = #{campaignid,jdbcType=BIGINT}
