mapper.select:
    SELECT t.`*`,g.name gname,u.name uname,m.name market FROM t_amz_settlement_acc_statement t
	LEFT JOIN t_amazon_group g ON g.id=t.groupid
	LEFT JOIN t_userinfo u ON u.id=t.operator
	LEFT JOIN t_marketplace m ON m.marketplaceId=t.marketplaceid
	where t.shopid=#{shopid,jdbcType=CHAR}

	  and   t.groupid=#{groupid,jdbcType=BIGINT}


	  and   t.marketplaceid=#{marketplaceid,jdbcType=CHAR}


	  and   ((t.startdate<=#{startdate,jdbcType=DATE} and t.enddate>=#{startdate,jdbcType=DATE} )
	          or   
	         (t.startdate<=#{enddate,jdbcType=DATE}   and t.enddate>=#{enddate,jdbcType=DATE}   )
	        )

	order by t.opttime desc

SELECT f.financial_event_group_start, i.name pname, i.`asin`, m.name mname, de.cname ftypename, IFNULL(p.location,p.url) image, t.posted_date,t.amazon_order_id,t.marketplace_name,t.event_type,t.sku,ftype,t.currency,t.amount,t.quantity FROM t_amz_settlement_open t LEFT join t_amz_fin_account f ON f.amazonAuthid=t.amazonauthid AND f.groupid=t.group_id LEFT JOIN t_marketplace m ON m.point_name=t.marketplace_name LEFT JOIN t_amazon_auth a ON a.id=t.amazonauthid LEFT JOIN t_product_info i ON i.marketplaceid=m.marketplaceId AND i.amazonAuthId=t.amazonauthid AND i.sku=t.sku LEFT JOIN t_picture p ON p.id=i.image LEFT JOIN t_amz_settlement_amount_description de ON de.ename=t.ftype WHERE a.shop_id=#{param.shopid,jdbcType=CHAR} and f.processing_status='Open'
and a.groupid=#{param.groupid,jdbcType=CHAR}
and m.marketplaceid=#{param.marketplaceid,jdbcType=CHAR}
and a.id=#{param.authid,jdbcType=CHAR}
and t.posted_date>=#{param.startDate,jdbcType=CHAR} and t.posted_date<=#{param.endDate,jdbcType=CHAR}
